import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { apiClient } from '../api/client';
import { useAuth } from '../contexts/AuthContext';

interface ProfileFormValues {
    email: string;
    profilePicture: string;
}

export const Profile = () => {
    const { user } = useAuth();

    const formik = useFormik<ProfileFormValues>({
        initialValues: {
            email: user?.email || '',
            profilePicture: user?.profilePicture || '',
        },
        validationSchema: Yup.object({
            email: Yup.string().email('Invalid email').required('Required'),
            profilePicture: Yup.string().url('Must be a valid URL'),
        }),
        onSubmit: async (values: ProfileFormValues) => {
            try {
                await apiClient.put('/profile', values);
                alert('Profile updated successfully');
            } catch (error) {
                alert('Failed to update profile');
            }
        },
    });

    return (
        <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-md mx-auto bg-white rounded-lg shadow p-8">
                <div className="text-center mb-8">
                    <img
                        className="mx-auto h-24 w-24 rounded-full"
                        src={user?.profilePicture || 'https://via.placeholder.com/150'}
                        alt={user?.username}
                    />
                    <h2 className="mt-4 text-2xl font-bold text-gray-900">{user?.username}</h2>
                </div>

                <form onSubmit={formik.handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                            Email
                        </label>
                        <input
                            id="email"
                            name="email"
                            type="email"
                            className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 focus:border-blue-500 focus:outline-none"
                            value={formik.values.email}
                            onChange={formik.handleChange}
                        />
                        {formik.errors.email && formik.touched.email && (
                            <p className="mt-1 text-sm text-red-600">{formik.errors.email}</p>
                        )}
                    </div>

                    <div>
                        <label htmlFor="profilePicture" className="block text-sm font-medium text-gray-700">
                            Profile Picture URL
                        </label>
                        <input
                            id="profilePicture"
                            name="profilePicture"
                            type="text"
                            className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 focus:border-blue-500 focus:outline-none"
                            value={formik.values.profilePicture}
                            onChange={formik.handleChange}
                        />
                        {formik.errors.profilePicture && formik.touched.profilePicture && (
                            <p className="mt-1 text-sm text-red-600">{formik.errors.profilePicture}</p>
                        )}
                    </div>

                    <button
                        type="submit"
                        className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                        Update Profile
                    </button>
                </form>
            </div>
        </div>
    );
};
