import React from 'react';
import { Box, Flex, Button, Avatar, Menu, MenuButton, MenuList, MenuItem } from '@chakra-ui/react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

export const Navbar = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    return (
        <Box as="nav" bg="white" boxShadow="sm" py={4}>
            <Flex maxW="container.xl" mx="auto" px={4} justify="space-between" align="center">
                <Link to="/">
                    <Box fontSize="xl" fontWeight="bold">Tongly</Box>
                </Link>

                {user ? (
                    <Flex align="center" gap={4}>
                        <Menu>
                            <MenuButton as={Button} variant="ghost" p={0}>
                                <Avatar size="sm" name={user.username} src={user.profilePicture} />
                            </MenuButton>
                            <MenuList>
                                <MenuItem onClick={() => navigate('/profile')}>Profile</MenuItem>
                                <MenuItem onClick={logout}>Logout</MenuItem>
                            </MenuList>
                        </Menu>
                    </Flex>
                ) : (
                    <Flex gap={4}>
                        <Button variant="ghost" onClick={() => navigate('/login')}>Login</Button>
                        <Button colorScheme="blue" onClick={() => navigate('/register')}>Sign Up</Button>
                    </Flex>
                )}
            </Flex>
        </Box>
    );
};
