import React from 'react';
import { Box, Button, FormControl, FormLabel, Input, VStack, useToast, Select } from '@chakra-ui/react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { apiClient } from '../api/client';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { RegisterData } from '../types';

interface RegisterResponse {
    token: string;
}

export const Register = () => {
    const { login } = useAuth();
    const navigate = useNavigate();
    const toast = useToast();

    const formik = useFormik<RegisterData>({
        initialValues: {
            username: '',
            password: '',
            email: '',
            role: 'student',
        },
        validationSchema: Yup.object({
            username: Yup.string().required('Required'),
            password: Yup.string().required('Required').min(6, 'Must be at least 6 characters'),
            email: Yup.string().email('Invalid email').required('Required'),
            role: Yup.string().oneOf(['student', 'tutor']).required('Required'),
        }),
        onSubmit: async (values: RegisterData) => {
            try {
                const response = await apiClient.post<RegisterResponse>('/auth/register', values);
                login(response.data.token);
                navigate('/');
            } catch (error) {
                toast({
                    title: 'Error',
                    description: 'Registration failed',
                    status: 'error',
                });
            }
        },
    });

    return (
        <Box maxW="md" mx="auto" mt={8} p={6} borderWidth={1} borderRadius="lg">
            <form onSubmit={formik.handleSubmit}>
                <VStack spacing={4}>
                    <FormControl>
                        <FormLabel>Username</FormLabel>
                        <Input
                            name="username"
                            value={formik.values.username}
                            onChange={formik.handleChange}
                            isInvalid={!!formik.errors.username && formik.touched.username}
                        />
                    </FormControl>
                    <FormControl>
                        <FormLabel>Email</FormLabel>
                        <Input
                            name="email"
                            type="email"
                            value={formik.values.email}
                            onChange={formik.handleChange}
                            isInvalid={!!formik.errors.email && formik.touched.email}
                        />
                    </FormControl>
                    <FormControl>
                        <FormLabel>Password</FormLabel>
                        <Input
                            name="password"
                            type="password"
                            value={formik.values.password}
                            onChange={formik.handleChange}
                            isInvalid={!!formik.errors.password && formik.touched.password}
                        />
                    </FormControl>
                    <FormControl>
                        <FormLabel>Role</FormLabel>
                        <Select
                            name="role"
                            value={formik.values.role}
                            onChange={formik.handleChange}
                        >
                            <option value="student">Student</option>
                            <option value="tutor">Tutor</option>
                        </Select>
                    </FormControl>
                    <Button type="submit" colorScheme="blue" width="full">
                        Register
                    </Button>
                </VStack>
            </form>
        </Box>
    );
};
