import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../api/client';

const languageEmojis: { [key: string]: string } = {
    'English': '🇬🇧', 'Spanish': '🇪🇸', 'French': '🇫🇷',
    'German': '🇩🇪', 'Chinese': '🇨🇳', 'Japanese': '🇯🇵',
    'Korean': '🇰🇷', 'Russian': '🇷🇺', 'Arabic': '🇸🇦',
    'Portuguese': '🇵🇹', 'Italian': '🇮🇹'
};

const interestEmojis: { [key: string]: string } = {
    'music': '🎵', 'movies': '🎬', 'books': '📚',
    'sports': '⚽', 'technology': '💻', 'art': '🎨',
    'cooking': '🍳', 'travel': '✈️', 'photography': '📷',
    'gaming': '🎮', 'nature': '🌿', 'fashion': '👗',
    'science': '🔬', 'history': '📜', 'business': '💼',
    'politics': '🏛️', 'health': '🏥', 'education': '🎓'
};

const goalEmojis: { [key: string]: string } = {
    'business': '💼', 'job': '💻', 'study': '📚',
    'trip': '✈️', 'migration': '🌍', 'exams': '📝',
    'culture': '🎭', 'friends': '👥', 'hobby': '🎯'
};

export const Survey = () => {
    const navigate = useNavigate();
    const { user, refreshUser } = useAuth();
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        nativeLanguage: '',
        learningLanguage: '',
        languageLevel: '',
        learningGoals: [] as string[],
        interests: [] as string[]
    });

    const handleNext = () => {
        if (step < 4) setStep(step + 1);
    };

    const handleBack = () => {
        if (step > 1) setStep(step - 1);
    };

    const handleSubmit = async () => {
        try {
            await api.user.updateProfile({
                ...formData,
                languages: [{
                    language: formData.learningLanguage,
                    level: formData.languageLevel
                }],
                survey_complete: true
            });
            await refreshUser();
            navigate('/dashboard');
        } catch (error) {
            console.error('Failed to update profile:', error);
        }
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return (
                    <div className="space-y-4">
                        <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                            What's your native language? 🗣️
                        </h2>
                        <div className="grid grid-cols-2 gap-4">
                            {Object.entries(languageEmojis).map(([lang, emoji]) => (
                                <button
                                    key={lang}
                                    onClick={() => {
                                        setFormData({ ...formData, nativeLanguage: lang });
                                        handleNext();
                                    }}
                                    className={`p-4 rounded-lg border ${
                                        formData.nativeLanguage === lang
                                            ? 'border-blue-500 bg-blue-50 dark:bg-blue-900'
                                            : 'border-gray-200 dark:border-gray-700'
                                    } hover:border-blue-500 transition-colors`}
                                >
                                    <span className="text-2xl mb-2">{emoji}</span>
                                    <span className="block text-sm text-gray-900 dark:text-white">{lang}</span>
                                </button>
                            ))}
                        </div>
                    </div>
                );

            case 2:
                return (
                    <div className="space-y-4">
                        <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                            Which language do you want to learn? 📚
                        </h2>
                        <div className="grid grid-cols-2 gap-4">
                            {Object.entries(languageEmojis)
                                .filter(([lang]) => lang !== formData.nativeLanguage)
                                .map(([lang, emoji]) => (
                                    <button
                                        key={lang}
                                        onClick={() => {
                                            setFormData({ ...formData, learningLanguage: lang });
                                            handleNext();
                                        }}
                                        className={`p-4 rounded-lg border ${
                                            formData.learningLanguage === lang
                                                ? 'border-blue-500 bg-blue-50 dark:bg-blue-900'
                                                : 'border-gray-200 dark:border-gray-700'
                                        } hover:border-blue-500 transition-colors`}
                                    >
                                        <span className="text-2xl mb-2">{emoji}</span>
                                        <span className="block text-sm text-gray-900 dark:text-white">{lang}</span>
                                    </button>
                                ))}
                        </div>
                    </div>
                );

            case 3:
                return (
                    <div className="space-y-4">
                        <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                            What's your current level? 📊
                        </h2>
                        <div className="grid grid-cols-2 gap-4">
                            {['A1', 'A2', 'B1', 'B2', 'C1', 'C2'].map((level) => (
                                <button
                                    key={level}
                                    onClick={() => {
                                        setFormData({ ...formData, languageLevel: level });
                                        handleNext();
                                    }}
                                    className={`p-4 rounded-lg border ${
                                        formData.languageLevel === level
                                            ? 'border-blue-500 bg-blue-50 dark:bg-blue-900'
                                            : 'border-gray-200 dark:border-gray-700'
                                    } hover:border-blue-500 transition-colors`}
                                >
                                    <span className="text-2xl mb-2">📝</span>
                                    <span className="block text-sm text-gray-900 dark:text-white">{level}</span>
                                    <span className="text-xs text-gray-500 dark:text-gray-400">
                                        {level.startsWith('A') ? 'Beginner' :
                                         level.startsWith('B') ? 'Intermediate' : 'Advanced'}
                                    </span>
                                </button>
                            ))}
                        </div>
                    </div>
                );

            case 4:
                return (
                    <div className="space-y-4">
                        <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                            What are your interests? 🎯
                        </h2>
                        <div className="grid grid-cols-3 gap-4">
                            {Object.entries(interestEmojis).map(([interest, emoji]) => (
                                <button
                                    key={interest}
                                    onClick={() => {
                                        const newInterests = formData.interests.includes(interest)
                                            ? formData.interests.filter(i => i !== interest)
                                            : [...formData.interests, interest];
                                        setFormData({ ...formData, interests: newInterests });
                                    }}
                                    className={`p-4 rounded-lg border ${
                                        formData.interests.includes(interest)
                                            ? 'border-blue-500 bg-blue-50 dark:bg-blue-900'
                                            : 'border-gray-200 dark:border-gray-700'
                                    } hover:border-blue-500 transition-colors`}
                                >
                                    <span className="text-2xl mb-2">{emoji}</span>
                                    <span className="block text-sm text-gray-900 dark:text-white capitalize">
                                        {interest}
                                    </span>
                                </button>
                            ))}
                        </div>
                    </div>
                );

            default:
                return null;
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-3xl mx-auto">
                <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                    <div className="mb-8">
                        <div className="flex justify-between items-center mb-4">
                            <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                                Let's personalize your experience
                            </h1>
                            <span className="text-sm text-gray-500 dark:text-gray-400">
                                Step {step} of 4
                            </span>
                        </div>
                        <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                            <div
                                className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                                style={{ width: `${(step / 4) * 100}%` }}
                            />
                        </div>
                    </div>

                    {renderStep()}

                    <div className="mt-8 flex justify-between">
                        <button
                            onClick={handleBack}
                            disabled={step === 1}
                            className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 
                                     disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            Back
                        </button>
                        {step === 4 ? (
                            <button
                                onClick={handleSubmit}
                                disabled={formData.interests.length === 0}
                                className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium
                                         hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 
                                         focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                Complete
                            </button>
                        ) : (
                            <button
                                onClick={handleNext}
                                disabled={
                                    (step === 1 && !formData.nativeLanguage) ||
                                    (step === 2 && !formData.learningLanguage) ||
                                    (step === 3 && !formData.languageLevel)
                                }
                                className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium
                                         hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 
                                         focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                Next
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Survey;
