import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useTheme } from '../../contexts/ThemeContext';
import { FaSun, FaMoon } from 'react-icons/fa';

export const Navbar = () => {
    const { isAuthenticated, user, logout } = useAuth();
    const { theme, toggleTheme } = useTheme();
    const navigate = useNavigate();

    return (
        <nav className="bg-white dark:bg-dark-card shadow-md">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex justify-between h-16">
                    <div className="flex">
                        <Link to="/" className="flex items-center">
                            <span className="text-xl font-bold text-primary-600 dark:text-primary-400">
                                Tongly
                            </span>
                        </Link>
                    </div>

                    <div className="flex items-center">
                        <button
                            onClick={toggleTheme}
                            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-dark-border"
                            aria-label="Toggle theme"
                        >
                            {theme === 'dark' ? <FaSun className="w-5 h-5" /> : <FaMoon className="w-5 h-5" />}
                        </button>

                        {isAuthenticated ? (
                            <div className="flex items-center ml-4">
                                <span className="text-gray-700 dark:text-gray-300">
                                    {user?.username}
                                </span>
                                <Link to="/profile" className="ml-4 text-primary-600 dark:text-primary-400">
                                    Profile
                                </Link>
                                <button
                                    onClick={logout}
                                    className="ml-4 text-red-600 dark:text-red-400"
                                >
                                    Logout
                                </button>
                            </div>
                        ) : (
                            <div className="flex items-center ml-4">
                                <Link to="/login" className="text-primary-600 dark:text-primary-400">
                                    Login
                                </Link>
                                <Link
                                    to="/register"
                                    className="ml-4 text-primary-600 dark:text-primary-400"
                                >
                                    Register
                                </Link>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </nav>
    );
};
